package com.example.wangchang.testbottomnavigationbar.adapter;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.bumptech.glide.Priority;import com.bumptech.glide.load.engine.DiskCacheStrategy;import com.bumptech.glide.request.RequestOptions;import com.example.wangchang.testbottomnavigationbar.R;import com.example.wangchang.testbottomnavigationbar.enity.Cast;import com.example.wangchang.testbottomnavigationbar.enity.Subject;import com.jude.easyrecyclerview.adapter.BaseViewHolder;import java.util.List;import butterknife.BindView;import butterknife.ButterKnife;/** * Created by liying on 2017-2-24. */public class BookHolder extends BaseViewHolder<Subject> {    @BindView(R.id.imageIv)    ImageView imageIv;    @BindView(R.id.book_title_tv)    TextView bookTitleTv;    @BindView(R.id.book_director_tv)    TextView bookDirectorTv;    @BindView(R.id.book_genres_tv)    TextView bookGenresTv;    @BindView(R.id.book_actor_tv)    TextView bookActorTv;    public BookHolder(ViewGroup parent) {        super(parent, R.layout.fragment_book_item);        ButterKnife.bind(this, itemView);    }    @Override    public void setData(Subject subjectData) {        super.setData(subjectData);        /*Glide.with(getContext())                .load(subjectData.getImages().getMedium())                .placeholder(R.mipmap.app_icon)                .centerCrop()                .crossFade()                .error(R.mipmap.app_icon)                .into(imageIv);*/        RequestOptions options = new RequestOptions()                .centerCrop()                .placeholder(R.mipmap.app_icon)                .error(R.drawable.icon_error)                .priority(Priority.HIGH)                .diskCacheStrategy(DiskCacheStrategy.AUTOMATIC);        Glide.with(getContext())                .load(subjectData.getImages().getMedium())                .apply(options)                .into(imageIv);        bookTitleTv.setText(subjectData.getTitle());        bookDirectorTv.setText("导演：" + subjectData.getDirectors().get(0).getName());        StringBuilder stringBuilder_genres = new StringBuilder();        List<String> genresList = subjectData.getGenres();        for (int i = 0; i < genresList.size(); i++) {            stringBuilder_genres.append(genresList.get(i));            if(i < genresList.size() - 1)             stringBuilder_genres.append("/");        }        bookGenresTv.setText("类型：" + stringBuilder_genres.toString());        StringBuilder stringBuilder_cast = new StringBuilder();        List<Cast> castList = subjectData.getCasts();        for (int i = 0; i < castList.size(); i++) {            stringBuilder_cast.append(castList.get(i).getName());            if(i < genresList.size() - 1)                stringBuilder_cast.append("/");        }        bookActorTv.setText("主演：" + stringBuilder_cast.toString());    }}