package com.example.wangchang.testbottomnavigationbar.adapter;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.TextView;import com.bumptech.glide.Glide;import com.bumptech.glide.load.engine.DiskCacheStrategy;import com.example.wangchang.testbottomnavigationbar.R;import com.example.wangchang.testbottomnavigationbar.enity.ResultsEntity;import com.jude.easyrecyclerview.adapter.BaseViewHolder;import butterknife.BindView;import butterknife.ButterKnife;/** * Created by liying on 2017-2-24. */public class GirlsViewHolder extends BaseViewHolder<ResultsEntity>{    @BindView(R.id.imageIv)    ImageView imageIv;    @BindView(R.id.descriptionTv)    TextView descriptionTv;    @BindView(R.id.home_card_title_tv)    TextView home_card_title_tv;    public GirlsViewHolder(ViewGroup parent) {        super(parent, R.layout.fragment_home_item);        ButterKnife.bind(this, itemView);    }    @Override    public void setData(ResultsEntity data) {        super.setData(data);        Glide.with(getContext())                .load(data.getUrl())                .diskCacheStrategy(DiskCacheStrategy.SOURCE)                .placeholder(R.mipmap.app_icon)                .crossFade()                .error(R.mipmap.app_icon)                .into(imageIv);        home_card_title_tv.setText("来源："+ data.getSource());        descriptionTv.setText(data.getCreatedAt()+ "  " + "作者：" + data.getWho());    }}