package com.example.wangchang.testbottomnavigationbar.enity;import android.os.Parcel;import android.os.Parcelable;/** * Created by liying on 2017-2-26. */public class Avatars implements Parcelable {    private String small;    private String medium;    private String large;    public String getSmall() {        return small;    }    public void setSmall(String small) {        this.small = small;    }    public String getMedium() {        return medium;    }    public void setMedium(String medium) {        this.medium = medium;    }    public String getLarge() {        return large;    }    public void setLarge(String large) {        this.large = large;    }    @Override    public int describeContents() {        return 0;    }    protected Avatars(Parcel in){        small = in.readString();        medium = in.readString();        large = in.readString();    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeString(this.small);        dest.writeString(this.medium);        dest.writeString(this.large);    }    /** 读取接口，目的是要从Parcel中构造一个实现了Parcelable的类的实例处理。* 因为实现类在这里还是不可知的，所以需要用到模板的方式，继承类名通过模板参数传入。* 为了能够实现模板参数的传入，这里定义Creator嵌入接口,内含两个接口函数分别返回单个和多个继承类实例。*/    public static final Parcelable.Creator<Avatars> CREATOR = new Parcelable.Creator<Avatars>() {        @Override        public Avatars createFromParcel(Parcel in) {            return new Avatars(in);        }        @Override        public Avatars[] newArray(int size) {            return new Avatars[size];        }    };}