package com.example.wangchang.testbottomnavigationbar.enity;import android.os.Parcel;import android.os.Parcelable;/** * Created by liying on 2017-2-26. */public class Cast implements Parcelable {    private String id;    private String name;    private String alt;    private Avatars avatars;    protected Cast(Parcel in) {        id = in.readString();        name = in.readString();        alt = in.readString();        avatars = in.readParcelable(Avatars.class.getClassLoader());    }    public static final Creator<Cast> CREATOR = new Creator<Cast>() {        @Override        public Cast createFromParcel(Parcel in) {            return new Cast(in);        }        @Override        public Cast[] newArray(int size) {            return new Cast[size];        }    };    public String getId() {        return id;    }    public void setId(String id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getAlt() {        return alt;    }    public void setAlt(String alt) {        this.alt = alt;    }    public Avatars getAvatars() {        return avatars;    }    public void setAvatars(Avatars avatars) {        this.avatars = avatars;    }    @Override    public String toString() {        return "cast.id=" + id + " cast.name=" + name + " | ";    }    @Override    public int describeContents() {        return 0;    }    @Override    public void writeToParcel(Parcel dest, int flags) {        dest.writeString(this.id);        dest.writeString(this.name);        dest.writeString(this.alt);        dest.writeParcelable(this.avatars,flags);    }}